body.script:
//event listener: DOM ready
function addLoadEvent(func) {
    var oldonload = window.onload;
    if (typeof window.onload != 'function') {
        window.onload = func;
    } else {
        window.onload = function () {
            if (oldonload) {
                oldonload();
            }
            func();
        }
    }
}
//call plugin function after DOM ready
addLoadEvent(function () {
    outdatedBrowser({
        bgColor: '#f25648',
        color: '#ffffff',
        lowerThan: 'js:Promise',
        languagePath: '/themes/_system/plugins/verifybrowser/lang/pl.html'
    })
});

div.script:
$('.dropdown-menu .dropdown-toggle').on('click', function (e) {
    if (!$(this).next().hasClass('show')) {
        $(this).parents('.dropdown-menu').first().find('.show').removeClass("show");
    }
    var $subMenu = $(this).next(".dropdown-menu");
    $subMenu.toggleClass('show');

    $(this).parents('li.nav-item.dropdown.show').on('hidden.bs.dropdown', function (e) {
        $('.dropdown-submenu .show').removeClass("show");
    });

    return false;
});

body.script:
$(document).ready(function() {
    var privacy = Cookies.get('prvSet');
    var privacy2 = Cookies.get('prvSet2');

    if (privacy == 'true') {
        $("#privacy_agree_block").prop('checked', true);
    } else {
        $("#privacy_agree_block").prop('checked', false);
    }

    if (privacy2 == 'true') {
        $("#privacy_agree_block_2").prop('checked', true);
    } else {
        $("#privacy_agree_block_2").prop('checked', false);
    }

    $("#privacy_agree_block").on("change", function() {
        Cookies.set('prvSet', this.checked, {
            expires: 30,
            path: '/'
        });
        if (!this.checked) {
            Cookies.set('popup', null, {
                path: '/'
            });
            Cookies.set('language', null, {
                path: '/'
            });
        }

        $(this).parent().find(".saved").fadeIn(300).delay(500).fadeOut(300);
    });

    $("#privacy_agree_block_2").on("change", function() {
        Cookies.set('prvSet2', this.checked, {
            expires: 30,
            path: '/'
        });

        $(this).parent().find(".saved").fadeIn(300).delay(500).fadeOut(300);
    });
});

